package ru.cpsmi.springdemo2019.web;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.stereotype.Controller;import org.springframework.ui.ModelMap;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RequestMethod;import ru.cpsmi.springdemo2019.model.Person;import ru.cpsmi.springdemo2019.service.PersonIntegrationService;@Controllerpublic class MainController {    @Autowired    private PersonIntegrationService service;    @RequestMapping(value = "/person/{id}", method = RequestMethod.GET)    public String personById(ModelMap model, @PathVariable(name = "id") String id) {        Person person = service.getPerson(id);        model.put("person", person);        return "personPage";    }    @RequestMapping(value = "/", method = RequestMethod.GET)    public String root(ModelMap model) {        return "index";    }}