package ru.cpsmi.springdemo2019.web;import org.springframework.beans.factory.annotation.Autowired;import org.springframework.web.bind.annotation.PathVariable;import org.springframework.web.bind.annotation.RequestMapping;import org.springframework.web.bind.annotation.RestController;import ru.cpsmi.springdemo2019.model.Person;import ru.cpsmi.springdemo2019.service.PersonIntegrationService;@RestController@RequestMapping("/api/person")public class PersonRestController {    @Autowired    private PersonIntegrationService personIntegrationService;    @RequestMapping("/{id}")    public Person getPerson(@PathVariable String id) {        return personIntegrationService.getPerson(id);    }}